import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getDetail } from '../../redux/actions'
import style from "./Card.module.css";
import { useParams } from 'react-router-dom';

function Card(props) {
    const dispatch = useDispatch()
    const details = useSelector(state => state.details)

    useEffect(() => {
        const params = useParams();
        dispatch(getDetail(props.match.params.id))
    }, [dispatch])

    const activities = details.activities?.map(e => {
        return {
            name: e.name,
            difficulty: e.difficulty,
            duration: e.duration,
            season: e.season
        }
    })

    return (
        <div>
            <div className={style.card}>
                    <div>
                        <div className={style.flag}>
                            <h2>{details.name}</h2>
                            <img src={details.image} alt={details.name} className={style.imagen} />
                        </div>
                        <div className={style.cont}>
                            <div className={style.detail}>
                                <div className={style.details}>
                                    <h3>Details</h3>
                                    <p>Code: {details.id}</p>
                                    <p>Continent: {details.continent}</p>
                                    <p>Capital: {details.capital}</p>
                                    <p>Population: {details.population}</p>
                                    <p>Subregion: {details.subregion}</p>
                                </div>
                                <div className={style.activities}>
                                    <h3>Activities</h3>
                                    {activities?.length > 0 ? activities?.map(e => {
                                        return (
                                            <div key={e.id}>
                                                <p>Name: {e.name}</p>
                                                <p>Difficulty: {e.difficulty}</p>
                                                <p>Duration: {e.duration}</p>
                                                <p>Season: {e.season}</p>
                                                <hr></hr>
                                            </div>
                                        )
                                    })
                                        : <p>Without activities</p>}
                                </div>
                            </div>
                        </div>
                    </div> : <p>Country not found</p>

            </div>
        </div>
    )
}

export default Card
